     * @var Product
     */
    private $product;

    /**
     * ConcreteBuilder1 constructor.
     */
    public function __construct()
    {
        $this->product = new Product();
    }

    public function buildPartA()
    {
        $this->product->add('部件A');
    }

    public function buildPartB()
    {
        $this->product->add('部件B');
